    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
      int n = Integer.valueOf(cx1.getText());
     if (n == 0 || n > 5){
         cx2.setText("Numero invalido");
     }
     switch (n){
         case 1:
             cx2.setText("Numero um");
             break;
         case 2 :
             cx2.setText("Numero dois");
             break;
         case 3 :
             cx2.setText("Numero trÃªs");
             break;
         case 4 : 
             cx2.setText("Numero quatro");
             break;
         case 5 :
             cx2.setText("Numero cinco");
             break;
             
     }
             
             
           
    }//GEN-LAST:event_jButton1ActionPerformed

